import {createSlice} from '@reduxjs/toolkit';
const initialState = [];
const cartSlice = createSlice({
    name:'cart',
    initialState,
    reducers:{
        add(state, action){
            state.push(action.payload);
        },
        increment(state, action){
            const updatedState = state.map((item) => {
                if(item.id===action.payload){
                    return {...item,quantity:item.quantity+1};
                }
                return item;
            })
            return updatedState;
        },
        decrement(state, action){
            const updatedState = state.map((item) => {
                if(item.id===action.payload&&item.quantity!==1){
                    return {...item,quantity:item.quantity-1};
                }
                return item;
            })
            return updatedState;
        },
        remove(state, action) {
            return state.filter((item) => item.id !== action.payload);
          },
        noOfProducts(state, action) {
            const totalQuantity = state.reduce((acc, item) => acc + item.quantity, 0);
            return totalQuantity;
        },
        totalCost(state,action){
            const totalCost = state.reduce((acc, item) => acc + item.quantity*item.price, 0);
            return totalCost;
        }
    }
});
export const {add, increment, remove,decrement,totalCost,no_of_products} = cartSlice.actions;
export default cartSlice.reducer;
