import React, { useEffect, useState } from 'react'
import '../product-grid/ProductGrid.css'
export const ProductGrid = () => {
    const [products,setProducts] = useState([]);
    useEffect(()=>{
        for(let i=1;i<=10;i++){
            const product=fetch(`https://fakestoreapi.com/products/${i}`)
            .then(res=>res.json())
            .then();
            setProducts(products,product);
        }
        console.log(products)
    },[])
  return (
    <div  className="product-grid">
        
        <div className="product" >
            <img alt="failed to load" style={{width:"100%",height:"100%"}} src="https://img.freepik.com/premium-vector/cosmetic-products-hair-care-vector-design_103044-2613.jpg?w=740"/>
            <div style={{width:"100%",display:"flex",boxShadow: "0 0 10px rgba(0, 0, 0, 0.2)",height:"2.25rem"}}>
                <div style={{marginLeft:"0.5rem",marginTop:"0.5rem",fontSize:"1.2rem"}}>Price: $123</div>
                <button style={{marginLeft:"auto"}}>Add to Cart</button>
            </div>
        </div>
        <div className="product">Product 2</div>
        <div className="product">Product 3</div>
        <div className="product">Product 4</div>
    </div>

  )
}
