import React, { useEffect, useState } from "react";
import { useSelector,useDispatch} from 'react-redux'; 
import "../cart/Cart.css";
import {remove} from '../../redux/reducer/reducer'
// Functional component for rendering cart items
// Define the styled table component

function CartItems({ cartItems, removeItemFromCart,increment, decrement }) {

  return (

      <div className="products">
        {cartItems.map((item, key) => (
          <div className="added-product" key={item.id}>
            <div className="product-details">
              <div className="product-image-container">
                <img src={item.image} alt="Your Image Alt Text" className="product-image" />
              </div>
              <div className="product-name">
                  <div>{item.name}</div>
              </div>
            </div>
            <div className="product-footer">
                <div style={{padding:"0.25rem",marginLeft:"4rem",display:"flex"}}>
                  <button onClick={()=>increment(key)} >+</button>
                  {item.quantity}
                  <button onClick={()=>decrement(key)} >-</button>
                </div>
                
                <div className="product-price">
                  <div>Total Price : </div>  
                  <div style={{fontSize:"1.25rem", color:"green",fontWeight:"bold"}}>${item.quantity*item.price}</div>
                </div>
                <button className="product-remove" onClick={()=>removeItemFromCart(item.id)}>
                  Remove
                </button>
            </div>
          </div>
        ))}
      </div>
  );
}

function Cart() {
  const [total, setTotal] = useState(0);
  const dispatch =useDispatch();
  // const cartItems = useSelector(state => state.cart.items);
  const [cartItems, setCartItems] = useState(useSelector(state => state.cart.items));
  useEffect(() => {
    // Calculate the total by summing the prices of all cart items
    const newTotal = cartItems.reduce((acc, cartItem) => {
      // console.log(acc+","+JSON.stringify(cartItem));
      return(acc + cartItem.quantity*cartItem.price)
    },0);
  
    // Update the total state
    setTotal(newTotal);
  
  }, [cartItems]);
  const increment=(item)=>{
    console.log(item)
    const updatedCart = cartItems.map((cartItem) =>
      cartItem.id === item ?({ ...cartItem, quantity: cartItem.quantity + 1} ): cartItem);
    setCartItems(updatedCart);
  };

  const decrement=(item)=>{
    console.log(item)
    const updatedCart = cartItems.map((cartItem) =>
      cartItem.id === item ?({ ...cartItem, quantity: cartItem.quantity - 1} ): cartItem);
    setCartItems(updatedCart);
  };

  const addItemToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const removeItemFromCart = (item) => {
    dispatch(remove())
    // console.log(JSON.stringify(updatedCart))
  };

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.price, 0);
  };

  return (
    <div>
      <div className="cart">
        
        <CartItems cartItems={cartItems} removeItemFromCart={removeItemFromCart} increment={increment} decrement={decrement} />

        <div className="price-details">
        
          <div className="price-details-heading" style={{fontFamily:"sans-serif",fontSize:"2.25rem",marginTop:"1.25rem",color:"#0082e6"}}>
            Price Details
          </div>
          <hr />
          <ul >
            <li className="price-detail-item">Price <div>{total}</div></li>
            <li className="price-detail-item">Discount <div>12%</div></li>
            <li className="price-detail-item">Delivery Charges <div>150</div></li>
          </ul>
          <hr style={{marginTop:"1.25rem"}}/>
          <div className="price-detail-item">Final Price<div>{(total*0.88)+150}</div></div>
        </div>
      </div>

    </div>
  );
}

export default Cart;
