import React, { useState } from "react";
import Navbar from '../navbar/Navbar'
import "../"
// Functional component for rendering cart items
// Define the styled table component

function CartItems({ cartItems, removeItemFromCart }) {
  return (
    <table className="table">
      <thead>
        <tr>
          <th>Header 1</th>
          <th>Header 2</th>
          <th>Header 3</th>
        </tr>
      </thead>
      <tbody>
        {data.map((row, index) => (
          <tr key={index}>
            <td>{row.column1}</td>
            <td>{row.column2}</td>
            <td>{row.column3}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

function Cart() {
  const [cartItems, setCartItems] = useState([
    {
      name:"Samsung",
      price:1234,
      id:1
    },
    {
      name:"Nokia",
      price:1234,
      id:2
    },
    {
      name:"Apple",
      price:1234,
      id:3
    },
    {
      name:"Oppo",
      price:1234,
      id:4
    },
    {
      name:"Vivo",
      price:1234,
      id:5
    },
  ]);
  
  const addItemToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const removeItemFromCart = (item) => {
    const updatedCart = cartItems.filter((cartItem) => cartItem.id !== item.id);
    setCartItems(updatedCart);
  };

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.price, 0);
  };

  return (
    <div>
      <Navbar/>
      <div  style={{marginTop:"4rem",position:"relative"}}>
        <div style={{backgroundColor:"#0082e6"}}>
          <h2>Shopping Cart</h2>
        </div>
        
        <CartItems cartItems={cartItems} removeItemFromCart={removeItemFromCart} />
        <p>Total Price: ${calculateTotalPrice()}</p>
      </div>
    </div>
  );
}

export default Cart;
