import React, { useState } from "react";
import Navbar from '../navbar/Navbar';
import "../cart/Cart.css";
// Functional component for rendering cart items
// Define the styled table component

function CartItems({ cartItems, removeItemFromCart }) {
  return (

      <div style={{width:"60%",marginLeft:"15px"}}>
        {cartItems.map((row, index) => (
          <div style={{width:"100%",padding:"0.25rem"}}>
            <div style={{borderRadius:"6px",display:"flex",height:"6rem",backgroundColor:"green"}}>
              <div style={{borderTopLeftRadius:"6px",width:"30%",backgroundColor:"blue"}}>
                <img></img>
              </div>
              <div style={{borderTopRightRadius:"6px",width:"70%",backgroundColor:"red"}}>
                  
              </div>
            </div>
            <div  style={{display:"flex",borderBottomRightRadius:"6px",borderBottomLeftRadius:"6px",width:"100%",backgroundColor:"white",height:"2.5rem",padding:"0.25rem"}}>
                <div style={{padding:"0.25rem"}}>
                  <button style={{height:"1.7rem",width:"1.7rem",borderRadius:"15px"}}>+</button>
                  {1231}
                  <button style={{height:"1.7rem",width:"1.7rem",borderRadius:"15px"}}>-</button>
                </div>
                <div style={{fontSize:"20px"}}>
                  $1234
                </div>
                <button style={{height:"2rem",width:"5rem",borderRadius:"10px",marginLeft:"auto",backgroundColor:"red",color:"white"}}>
                  Remove
                </button>

            </div>
          </div>
        ))}
      </div>
  );
}

function Cart() {
  const [cartItems, setCartItems] = useState([
    {
      name:"Samsung",
      price:1234,
      id:1
    },
    {
      name:"Nokia",
      price:1234,
      id:2
    },
    {
      name:"Apple",
      price:1234,
      id:3
    },
    {
      name:"Oppo",
      price:1234,
      id:4
    },
    {
      name:"Vivo",
      price:1234,
      id:5
    },
  ]);
  
  const addItemToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const removeItemFromCart = (item) => {
    const updatedCart = cartItems.filter((cartItem) => cartItem.id !== item.id);
    setCartItems(updatedCart);
  };

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.price, 0);
  };

  return (
    <div>
      <Navbar/>
      {/* <div  style={{marginTop:"4rem",position:"relative"}}>
        <div style={{color:"#0082e6"}}>
          <h2 style={{float:"left",fontSize:"2.5rem",fontFamily:"monospace"}}>Your Cart</h2>
        </div>
        
        
        <p>Total Price: ${calculateTotalPrice()}</p>
      </div> */}
      <div style={{borderRadius:"4px", minHeight: "25rem", display: "flex", marginTop: "50px", padding: "5px", backgroundColor: "skyblue" }}>
        {/* Check styles in CartItems component */}
        <CartItems cartItems={cartItems} removeItemFromCart={removeItemFromCart} />

        <div style={{ right: "0",borderRadius:"4px",position: "fixed", height: "100vh", width: "37%", backgroundColor: "pink", padding: "5px",marginRight:"10px" }}>
          {/* Make sure the content inside this div doesn't have conflicting styles */}
          <div>
            {/* Content */}
          </div>
        </div>
      </div>


    </div>
  );
}

export default Cart;
