import React, { useEffect, useState } from 'react'
import '../slider/slider.css'



const images=[
    "https://cssslider.com/sliders/demo-17/data1/images/picjumbo.com_hnck0391.jpg",
    "https://cssslider.com/sliders/demo-17/data1/images/picjumbo.com_img_5948.jpg",
    "https://cssslider.com/sliders/demo-17/data1/images/picjumbo.com_hanv9909.jpg",
    "https://cssslider.com/sliders/demo-17/data1/images/picjumbo.com_img_6122.jpg"
]
 export const Slider= () => {
    const [image,setImage]= useState();
    const [imageIndex,setImageIndex]= useState(0);
    useEffect(()=>{
        setImage(images[imageIndex]);
    },[])

    const changesliderimg=(item)=>{
        const no_of_slides=4;
        if(item===0){
            setImageIndex((imageIndex+1)%no_of_slides);
        }else{
            setImageIndex((imageIndex-1)%no_of_slides);
        }


        setImage(images[imageIndex]);
    }
  return (
    <div className='slider-container'>
        <div className='slider' s >
            <button style={{ position: 'absolute', top: '40%', marginLeft:"10px" }} onClick={()=>changesliderimg(1)}>+</button>
            <img src={image}/> 
            <button style={{ position: 'absolute', top: '40%', marginRight:"-5rem" }} onClick={()=>changesliderimg(0)}>-</button>
        </div>
    </div>

  )
}
