import React, { useState } from "react";
import Navbar from '../navbar/Navbar';
import "../cart/Cart.css";
// Functional component for rendering cart items
// Define the styled table component

function CartItems({ cartItems, removeItemFromCart }) {
  return (

      <div style={{width:"60%",marginLeft:"15px"}}>
        {cartItems.map((item, key) => (
          <div style={{width:"100%",padding:"0.25rem"}}>
            <div style={{borderRadius:"6px",display:"flex",height:"8rem",backgroundColor:"white",boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)"}}>
              <div style={{ borderTopLeftRadius: "6px", width: "30%", backgroundColor: "blue" }}>
                <img src={item.image} alt="Your Image Alt Text" style={{ width: "100%", height: "100%", display: "block", borderTopLeftRadius: "6px" }} />
              </div>
              <div style={{borderTopRightRadius:"6px",width:"70%",backgroundColor:"white",boxShadow: "0 0 10px rgba(0, 0, 0, 0.05)"}}>
                  <div>{item.name}</div>

              </div>
            </div>
            <div  style={{display:"flex",borderBottomRightRadius:"6px",borderBottomLeftRadius:"6px",width:"100%",backgroundColor:"white",height:"2.5rem",padding:"0.25rem",boxShadow: "0 0 10px rgba(0, 0, 0, 0.2)"}}>
                <div style={{padding:"0.25rem",marginLeft:"4rem"}}>
                  <button onClick={()=>increment(item.id)} style={{border: "none" ,height:"1.7rem",width:"1.7rem",borderRadius:"15px",backgroundColor:"#0082e6",color:"white"}}>+</button>
                  {item.quantity}
                  <button onClick={()=>decrement(item.id)}style={{border: "none" ,height:"1.7rem",width:"1.7rem",borderRadius:"15px",backgroundColor:"#0082e6",color:"white"}}>-</button>
                </div>
                
                <div style={{display:"flex",marginLeft:"4rem",fontSize:"1.1rem",marginTop:"0.65rem"}}>
                  <div>Total Price : </div>  
                  <div style={{fontSize:"1.25rem", color:"green",fontWeight:"bold"}}>$1234</div>
                </div>
                <button onClick={()=>removeItemFromCart(key)} style={{border: "none" ,height:"2rem",width:"5rem",borderRadius:"10px",marginLeft:"auto",backgroundColor:"red",color:"white"}}>
                  Remove
                </button>

            </div>
          </div>
        ))}
      </div>
  );
}

function Cart() {
  const [cartItems, setCartItems] = useState([
    {
      quantity:"1",
      name:"Kayamchurna 500ml",
      price:349,
      image:"https://m.media-amazon.com/images/I/71rGwR2dSaL.SS700.jpg",
      id:1
    },
    {
      quantity:"1",
      name:"Nokia Phone",
      price:25000,
      image:"https://i5.walmartimages.com/asr/cb753135-5673-4826-9bdf-575461d03656.98d74c4506fc360c623717342bbbdfb1.jpeg",
      id:2
    },
    {
      quantity:"1",
      name:"Bucket",
      price:220,
      image:"https://th.bing.com/th/id/OIP.6vaMDz4TMtTF6ZdByIQuZgHaHa?pid=ImgDet&rs=1",
      id:3
    },
    {
      quantity:"1",
      name:"Acoustic Guitar",
      price:5500,
      image:"https://th.bing.com/th/id/OIP.jeIY9-NDMOdsjM6AFqEXqQHaHa?pid=ImgDet&rs=1",
      id:4
    },
    {
      quantity:"1",
      name:"Biscuits",
      price:30,
      image:"https://i5.walmartimages.com/asr/025cc850-e38c-42f6-8339-9d8a56379f9e_1.3631b64f9b52ab37a80e79ff55b56e7a.jpeg",
      id:5
    },
  ]);
  const increment=(id)=>{
    const cartitem = cartItems.filter((cartItem) => cartItem.id == id);
    const cartitems = cartItems.filter((cartItem) => cartItem.id != id);
    setCartItems(cartitems);
    cartitem.quantity++;
    setCartItems([...cartItems, cartitem]);
  }

  const decrement=(id)=>{
    const cartitem = cartItems.filter((cartItem) => cartItem.id == id);
    const cartitems = cartItems.filter((cartItem) => cartItem.id != id);
    setCartItems(cartitems);
    cartitem.quantity--;
    setCartItems([...cartItems, cartitem]);
  }
  const addItemToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const removeItemFromCart = (item) => {
    const updatedCart = cartItems.filter((cartItem) => cartItem.id !== item.id);
    setCartItems(updatedCart);
  };

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.price, 0);
  };

  return (
    <div>
      <Navbar/>
      {/* <div  style={{marginTop:"4rem",position:"relative"}}>
        <div style={{color:"#0082e6"}}>
          <h2 style={{float:"left",fontSize:"2.5rem",fontFamily:"monospace"}}>Your Cart</h2>
        </div>
        
        
        <p>Total Price: ${calculateTotalPrice()}</p>
      </div> */}
      <div style={{borderRadius:"4px", minHeight: "25rem", display: "flex", marginTop: "50px", padding: "5px" }}>
        {/* Check styles in CartItems component */}
        <CartItems cartItems={cartItems} removeItemFromCart={removeItemFromCart} />

        <div style={{ right: "0",borderRadius:"4px",position: "fixed", height: "60vh", width: "37%", padding: "5px",marginRight:"10px",boxShadow: "0 0 10px rgba(0, 0, 0, 0.2)" }}>
          {/* Make sure the content inside this div doesn't have conflicting styles */}
          <div style={{fontSize:"2rem",marginTop:"1.25rem"}}>
            Price Details
          </div>
          <hr style={{marginTop:"1.25rem"}}/>
          <ul style={{marginTop:"1.25rem"}}>
            <li style={{display:"flex",fontSize:"1.25rem",marginTop:"1.25rem",marginLeft:"3rem"}}>Price <div style={{marginLeft:"auto",marginRight:"3rem"}}>123</div></li>
            <li style={{display:"flex",fontSize:"1.25rem",marginTop:"1.25rem",marginLeft:"3rem"}}>Discount <div style={{marginLeft:"auto",marginRight:"3rem"}}>11</div></li>
            <li style={{display:"flex",fontSize:"1.25rem",marginTop:"1.25rem",marginLeft:"3rem"}}>Delivery Charges <div style={{marginLeft:"auto",marginRight:"3rem"}}>12%</div></li>
          </ul>
          <hr style={{marginTop:"1.25rem"}}/>
          <div style={{display:"flex",fontSize:"1.45rem",marginTop:"1.25rem",marginLeft:"11rem"}}>Total Amount </div>
        </div>
      </div>
      <div className='btm-nav'>
                <input type="checkbox" id="check" className="check-input" />
                <ul className="nav-links">
                    <li className='menu-item'><a href="#home">Home</a></li>
                    <li className='menu-item'><a href="#about">Clothing</a></li>
                    <li className='menu-item'><a href="#services">Electronics</a></li>
                    <li className='menu-item'><a href="#contact">Groceries</a></li>
                </ul>
            </div>

    </div>
  );
}

export default Cart;
