    const handleClick = () => {
        setButtonClicked(!buttonClicked);
      }
    return(
        <div className="product" >
            <img alt="failed to load" style={{width:"100%",height:"80%"}} src={item.image}/>
            <div>
                {item.title.length > 20 ? `${item.title.substr(0, 20)}....` : item.title}
            </div>
            <div style={{backgroundColor:"white",width:"100%",display:"flex",height:"2rem"}}>
                <div style={{fontSize:"1.2rem",marginTop:"0.4rem"}}>
                    â‚¹ {item.price}
                </div>
                <button onClick={handleClick} className={buttonClicked ? 'clicked' : 'notclicked'}>
                    Add to Cart
                </button>
            </div>
        </div>
    );  
}
export const ProductGrid = () => {
    const [products,setProducts] = useState([]);

    useEffect(()=>{
        const fetchData = async () => {
            const promises = Array.from({ length: 12 }, (_, i) =>
              fetch(`https://fakestoreapi.com/products/${i + 1}`).then((res) => res.json())
            );
          
            const productsData = await Promise.all(promises);
            
            setProducts(productsData);
        };
          fetchData();
    },[])

  return (
    <div  className="product-grid">
        {console.log(products)}
        {products.map((item, index) => (
                <Product item/>
        ))}
    </div>

  )
}

const products = (product) => {
    return(
        <div className="product" >
            <img alt="failed to load" style={{width:"100%",height:"100%"}} src={product.image}/>
            <div style={{width:"100%",display:"flex",boxShadow: "0 0 10px rgba(0, 0, 0, 0.2)",height:"2.25rem"}}>
                <div style={{marginLeft:"0.5rem",marginTop:"0.5rem",fontSize:"1.2rem"}}>Price: {product.price}</div>
                <button style={{marginLeft:"auto"}}>Add to Cart</button>
            </div>
        </div>
    );
}
