import React, { useState } from "react";
import Navbar from '../navbar/Navbar'
// Functional component for rendering cart items
function CartItems({ cartItems, removeItemFromCart }) {
  return (
    <ul>
      {cartItems.map((item) => (
        <li key={item.id}>
          {item.name} - ${item.price}
          <button onClick={() => removeItemFromCart(item)}>Remove</button>
        </li>
      ))}
    </ul>
  );
}

function Cart() {
  const [cartItems, setCartItems] = useState([
    {
      name:"Samsung",
      price:1234,
      id:1
    },
    {
      name:"Nokia",
      price:1234,
      id:2
    },
    {
      name:"Apple",
      price:1234,
      id:3
    },
    {
      name:"Oppo",
      price:1234,
      id:4
    },
    {
      name:"Vivo",
      price:1234,
      id:5
    },
  ]);
  
  const addItemToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const removeItemFromCart = (item) => {
    const updatedCart = cartItems.filter((cartItem) => cartItem.id !== item.id);
    setCartItems(updatedCart);
  };

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.price, 0);
  };

  return (
    <div>
      <Navbar/>
      <div  style={{marginTop:"4rem",position:"relative"}}>
        <h2 >Shopping Cart</h2>
        <CartItems cartItems={cartItems} removeItemFromCart={removeItemFromCart} />
        <p>Total Price: ${calculateTotalPrice()}</p>
      </div>
    </div>
  );
}

export default Cart;
