import React, { useState } from "react";
import Navbar from '../navbar/Navbar';
import "../cart/Cart.css";
// Functional component for rendering cart items
// Define the styled table component

function CartItems({ cartItems, removeItemFromCart }) {
  return (
    <table className="table">
      <thead>
        <tr>
          <th>Id</th>
          <th>Name</th>
          <th>Price</th>
          <th>Remove</th>
        </tr>
      </thead>
      <tbody>
        {cartItems.map((row, index) => (
          <div style={{width:"100%"}}>
            <div style={{display:"flex",height:"6rem",backgroundColor:"green"}}>
              <div style={{width:"30%",backgroundColor:"blue"}}>
                <img></img>
              </div>
            <div style={{width:"70%",backgroundColor:"red"}}>
                
            </div>
            </div>
            <div  style={{width:"100%",backgroundColor:"yellow",height:"3rem"}}>

            </div>
          </div>
        ))}
      </tbody>
    </table>
  );
}

function Cart() {
  const [cartItems, setCartItems] = useState([
    {
      name:"Samsung",
      price:1234,
      id:1
    },
    {
      name:"Nokia",
      price:1234,
      id:2
    },
    {
      name:"Apple",
      price:1234,
      id:3
    },
    {
      name:"Oppo",
      price:1234,
      id:4
    },
    {
      name:"Vivo",
      price:1234,
      id:5
    },
  ]);
  
  const addItemToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const removeItemFromCart = (item) => {
    const updatedCart = cartItems.filter((cartItem) => cartItem.id !== item.id);
    setCartItems(updatedCart);
  };

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.price, 0);
  };

  return (
    <div>
      <Navbar/>
      {/* <div  style={{marginTop:"4rem",position:"relative"}}>
        <div style={{color:"#0082e6"}}>
          <h2 style={{float:"left",fontSize:"2.5rem",fontFamily:"monospace"}}>Your Cart</h2>
        </div>
        
        
        <p>Total Price: ${calculateTotalPrice()}</p>
      </div> */}
      <div style={{minHeight:"25rem",display:"flex",marginTop:"50px",padding:"10px",backgroundColor:"skyblue"}}>

        <div style={{width:"60%",marginLeft:"15px"}}>
          <CartItems cartItems={cartItems} removeItemFromCart={removeItemFromCart} />
        </div>

        <div style={{height:"24rem",width:"40%",marginLeft:"15px",backgroundColor:"pink",padding:"10px"}}>

        </div>
      </div>

    </div>
  );
}

export default Cart;
